// src/components/ProjectCard.js

export default function ProjectCard({ image, title, description, technologies, codeLink, liveLink }) {
    return (
      <div className="max-w-4xl mx-auto my-8 bg-white dark:bg-[#1a1a1a] shadow-xl rounded-2xl overflow-hidden flex flex-col md:flex-row">
        <img src={image} alt={title} className="w-full md:w-1/2 object-cover" />

        <div className="p-6 flex flex-col justify-between w-full md:w-1/2">
          <div>
            <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-2">{title}</h2>
            <p className="text-gray-600 dark:text-gray-300 mb-4">{description}</p>
            <div className="flex flex-wrap gap-2 mb-4">
              {technologies.map((tech, index) => (
                <img
                  key={index}
                  src={tech}
                  alt={`tech-${index}`}
                  className="h-6 w-6"
                />
              ))}
            </div>
          </div>

          <div className="flex gap-4">
            <a
              href={codeLink}
              target="_blank"
              rel="noopener noreferrer"
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
            >
              Code
            </a>
            <a
              href={liveLink}
              target="_blank"
              rel="noopener noreferrer"
              className="px-4 py-2 bg-gray-800 text-white rounded-lg hover:bg-gray-900 transition"
            >
              Live
            </a>
          </div>
        </div>
      </div>
    );
  }
