// ProjectCard.js

export default function ProjectCard({ image, title, description, technologies, codeLink, liveLink }) {
  return (
    <div className="max-w-4xl mx-auto my-8 bg-white shadow-xl rounded-2xl overflow-hidden flex">
      <img src={image} alt={title} className="w-1/2 object-cover" />
      <div className="p-6 w-1/2">
        <h2 className="text-2xl font-semibold mb-4">{title}</h2>
        <p className="mb-4 text-gray-600">{description}</p>
        <div className="flex items-center gap-2 mb-4">
          {technologies.map((tech, index) => (
            <img key={index} src={tech} alt={`tech-${index}`} className="h-6 w-6" />
          ))}
        </div>
        <div className="flex gap-4">
          <a href={codeLink} target="_blank" rel="noopener noreferrer" className="px-4 py-2 bg-blue-600 text-white rounded-lg">Code</a>
          <a href={liveLink} target="_blank" rel="noopener noreferrer" className="px-4 py-2 bg-gray-800 text-white rounded-lg">Live</a>
        </div>
      </div>
    </div>
  );
}
