import '../styles/footer.css';
import { useState, useEffect } from 'react';

function Footer() {
  const email = "alfonsojmoragav@gmail.com";
  const [copied, setCopied] = useState(false);
  const [hovered, setHovered] = useState(false);
  const [snakeSrc, setSnakeSrc] = useState(null);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(email).then(() => {
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    });
  };

  useEffect(() => {
    const updateSnake = () => {
      const timestamp = new Date().getTime();
      setSnakeSrc(`https://raw.githubusercontent.com/poncho-ajmv/poncho-ajmv/output/github-snake-dark.svg?${timestamp}`);
    };

    updateSnake(); // Cargar al montar

    // Refrescar cada 12 horas (en milisegundos)
    const interval = setInterval(updateSnake, 12 * 60 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <footer className="footer">
      <label className="footer-label">üìß Email</label>
      <div className="footer-form-style">
        <input type="text" value={email} disabled />
        <a href={`mailto:${email}`} className="icon-button" title="Enviar">‚û§</a>
        <div
          className="tooltip-container"
          onMouseEnter={() => setHovered(true)}
          onMouseLeave={() => setHovered(false)}
          onClick={copyToClipboard}
        >
          <button className="icon-button">üìã</button>
          {hovered && (
            <span className="tooltip">{copied ? 'Copied' : 'Copy'}</span>
          )}
        </div>
      </div>

      <div className="github-snake">
        <h2>üêç GitHub Activity Snake</h2>
        {snakeSrc && (
          <img
            alt="github-snake"
            src={snakeSrc}
            style={{
              maxWidth: '100%',
              marginTop: '1rem',
              imageRendering: 'pixelated',
              filter: 'blur(0.1px) contrast(1)',
            }}
            loading="lazy"
            decoding="async"
          />
        )}
      </div>

      <p className="made-by">Hecho con üíª por poncho_ajmv</p>
    </footer>
  );
}

export default Footer;
