// src/App.js
import './App.css';
import MainHeader from './components/MainHeader';
import Experience from './components/Experience';
import Skills from './components/Skills';
import DiscordStatus from './components/DiscordStatus';
import Socials from './components/Socials';
import Footer from './components/Footer';
import ProjectCard from './components/ProjectCard'; // Importa el componente
import { useState, useEffect } from 'react';

function App() {
  const [darkMode, setDarkMode] = useState(true);

  useEffect(() => {
    document.body.className = darkMode ? 'dark-body' : 'light-body';
  }, [darkMode]);

  return (
    <div className={darkMode ? 'app dark' : 'app light'}>
      <MainHeader darkMode={darkMode} setDarkMode={setDarkMode} />

      {/* Nuevo Proyecto */}
      <section id="projects" className="bg-[#eaf0ff] py-16">
        <div className="max-w-6xl mx-auto px-4">
          <ProjectCard
            image="/images/music-app.png"
            title="Music App"
            description="Play, create playlists, like songs and discover music trends."
            technologies={[
              "/icons/nextjs.svg",
              "/icons/react.svg",
              "/icons/typescript.svg",
              "/icons/tailwind.svg",
              "/icons/redis.svg",
            ]}
            codeLink="https://github.com/usuario/music-app"
            liveLink="https://music-app-demo.vercel.app"
          />
        </div>
      </section>


      <section id="experience">
        <Experience />
      </section>

      <div className="side-by-side-section" id="skills">
        <Skills />
        <DiscordStatus />
      </div>

      <section id="contact">
        <Socials />
        <Footer />
      </section>
    </div>
  );
}

export default App;
