import './App.css';
import MainHeader from './components/MainHeader';
import Skills from './components/Skills';
import DiscordStatus from './components/DiscordStatus';
import Socials from './components/Socials';
import Footer from './components/Footer';
import ProjectCard from './components/ProjectCard';
import TerminalSimulator from './components/TerminalSimulator'; // ← Importar acá
import { useState, useEffect } from 'react';

function App() {
  const [darkMode, setDarkMode] = useState(true);

  useEffect(() => {
    document.body.className = darkMode ? 'dark-body' : 'light-body';
  }, [darkMode]);

  useEffect(() => {
  // Bloquear cualquier tipo de scroll automático
  const preventAutoScroll = () => {
    window.scrollTo({ top: 0, left: 0, behavior: 'auto' });
  };

  // Ejecutar justo después del render
  setTimeout(preventAutoScroll, 100);

  // También cuando el usuario cambia de tamaño la ventana
  window.addEventListener('resize', preventAutoScroll);
  window.addEventListener('hashchange', preventAutoScroll);

  return () => {
    window.removeEventListener('resize', preventAutoScroll);
    window.removeEventListener('hashchange', preventAutoScroll);
  };
}, []);



  return (
    <div className={darkMode ? 'app dark' : 'app light'}>
      <MainHeader darkMode={darkMode} setDarkMode={setDarkMode} />
       {/* Puedes colocarlo donde quieras */}
       <TerminalSimulator />

      <div className="side-by-side-section" id="skills">
        <Skills />
        <DiscordStatus />
      </div>

      <ProjectCard darkMode={darkMode} />

      <div className="cta-mario-block">
        <p className="cta-mario-line">
          <span className="prompt">&gt;</span>  Looking for an engineer? I’m ready for new challenges.

        </p>
      </div>




      <section id="contact">
        <Socials />
        <Footer />
      </section>
    </div>
  );
}

export default App;
