import '../styles/footer.css';
import { useState, useEffect } from 'react';

function Footer() {
  const email = "alfonsojmoragav@gmail.com";
  const [copied, setCopied] = useState(false);
  const [hovered, setHovered] = useState(false);
  const [snakeUrl, setSnakeUrl] = useState(null);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(email).then(() => {
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    });
  };

  const loadSnake = async () => {
    try {
      const res = await fetch(`https://raw.githubusercontent.com/poncho-ajmv/poncho-ajmv/output/github-snake-dark.svg?${Date.now()}`);
      const blob = await res.blob();
      const objectUrl = URL.createObjectURL(blob);
      setSnakeUrl(objectUrl);
    } catch (err) {
      console.error('Error cargando snake:', err);
    }
  };

  useEffect(() => {
    loadSnake();

    const timer = setTimeout(() => {
      loadSnake(); // Recargar cada 12h
    }, 12 * 60 * 60 * 1000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <footer className="footer">
      <label className="footer-label">ðŸ“§ Email</label>
      <div className="footer-form-style">
        <input type="text" value={email} disabled />
        <a href={`mailto:${email}`} className="icon-button" title="Enviar">âž¤</a>
        <div
          className="tooltip-container"
          onMouseEnter={() => setHovered(true)}
          onMouseLeave={() => setHovered(false)}
          onClick={copyToClipboard}
        >
          <button className="icon-button">ðŸ“‹</button>
          {hovered && (
            <span className="tooltip">{copied ? 'Copied' : 'Copy'}</span>
          )}
        </div>
      </div>

      {snakeUrl && (
        <div className="github-snake">
          <img
            alt="Animated snake showing GitHub activity of poncho_ajmv"
            src={snakeUrl}
            style={{
              maxWidth: '100%',
              marginTop: '1rem',
              imageRendering: 'pixelated',
              filter: 'blur(0.4px) contrast(0.95)',
              opacity: 0.7,
            }}
            loading="lazy"
            decoding="async"
          />
        </div>
      )}

      <p className="made-by">Hecho con ðŸ’» por poncho_ajmv</p>
    </footer>
  );
}

export default Footer;
