import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

// ðŸ›‘ Bloquear scroll inmediatamente
document.body.style.overflowY = 'hidden';

const root = ReactDOM.createRoot(document.getElementById('root'));

root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// âœ… Opcional: liberar scroll despuÃ©s de montar (con pequeÃ±o delay para evitar autoscroll visual)
setTimeout(() => {
  window.scrollTo(0, 0); // Asegura que se quede arriba
  document.body.style.overflowY = 'auto';
}, 100); // 100ms suele ser suficiente

// MÃ©tricas (opcional)
reportWebVitals();
